import sgMail from "@sendgrid/mail";
import twilio from "twilio";

const sendgridApiKey = process.env.SENDGRID_API_KEY;
const twilioAccountSid = process.env.TWILIO_ACCOUNT_SID;
const twilioAuthToken = process.env.TWILIO_AUTH_TOKEN;
const twilioPhoneNumber = process.env.TWILIO_PHONE_NUMBER;

if (sendgridApiKey) {
  sgMail.setApiKey(sendgridApiKey);
}

const twilioClient = twilioAccountSid && twilioAuthToken 
  ? twilio(twilioAccountSid, twilioAuthToken) 
  : null;

export async function sendEmail(to: string, subject: string, text: string, html?: string) {
  if (!sendgridApiKey) {
    console.log(" Email (SendGrid not configured):", { to, subject, text });
    return;
  }

  try {
    await sgMail.send({
      to,
      from: "logistics@logisticspro.com", // You should verify this domain in SendGrid
      subject,
      text,
      html: html || text,
    });
    console.log(" Email sent to:", to);
  } catch (error) {
    console.error("Email error:", error);
  }
}

export async function sendSMS(to: string, body: string) {
  if (!twilioClient || !twilioPhoneNumber) {
    console.log(" SMS (Twilio not configured):", { to, body });
    return;
  }

  try {
    await twilioClient.messages.create({
      body,
      from: twilioPhoneNumber,
      to,
    });
    console.log(" SMS sent to:", to);
  } catch (error) {
    console.error("SMS error:", error);
  }
}

export async function notifyOrderStatusChange(
  userEmail: string,
  userPhone: string | null,
  orderNumber: string,
  oldStatus: string,
  newStatus: string
) {
  const emailSubject = `Order ${orderNumber} Status Update`;
  const message = `Your order ${orderNumber} status has been updated from "${oldStatus}" to "${newStatus}".`;

  await sendEmail(userEmail, emailSubject, message);

  if (userPhone && (newStatus === "in-transit" || newStatus === "delivered")) {
    await sendSMS(userPhone, message);
  }
}

client/src/components/google-map.tsx.content:
import { useEffect, useRef } from "react";

interface GoogleMapProps {
  center?: { lat: number; lng: number };
  markers?: Array<{
    position: { lat: number; lng: number };
    title?: string;
    icon?: string;
  }>;
  zoom?: number;
  height?: string;
}

export function GoogleMap({ 
  center = { lat: 40.7128, lng: -74.0060 }, 
  markers = [], 
  zoom = 12,
  height = "400px" 
}: GoogleMapProps) {
  const mapRef = useRef<HTMLDivElement>(null);
  const mapInstanceRef = useRef<google.maps.Map | null>(null);
  const markersRef = useRef<google.maps.Marker[]>([]);

  useEffect(() => {
    if (!mapRef.current) return;

    const initMap = () => {
      if (!window.google) {
        console.error("Google Maps API not loaded");
        return;
      }

      if (!mapInstanceRef.current) {
        mapInstanceRef.current = new google.maps.Map(mapRef.current!, {
          center,
          zoom,
          styles: [
            {
              featureType: "all",
              elementType: "labels.text.fill",
              stylers: [{ color: "#6b7280" }],
            },
          ],
        });
      }

      markersRef.current.forEach(marker => marker.setMap(null));
      markersRef.current = [];

      if (markers.length > 0) {
        const bounds = new google.maps.LatLngBounds();

        markers.forEach(({ position, title, icon }) => {
          const latLng = new google.maps.LatLng(position.lat, position.lng);
          const marker = new google.maps.Marker({
            position: latLng,
            map: mapInstanceRef.current!,
            title,
            icon: icon || undefined,
          });
          markersRef.current.push(marker);
          bounds.extend(latLng);
        });

        mapInstanceRef.current!.fitBounds(bounds);
      } else {
        mapInstanceRef.current!.setCenter(center);
        mapInstanceRef.current!.setZoom(zoom);
      }
    };

    if (window.google) {
      initMap();
    } else {
      const existingScript = document.querySelector('script[src*="maps.googleapis.com"]');
      if (existingScript) {
        existingScript.addEventListener('load', initMap);
      } else {
        const apiKey = import.meta.env.VITE_GOOGLE_MAPS_API_KEY || 'DEMO_KEY';
        const script = document.createElement("script");
        script.src = `https://maps.googleapis.com/maps/api/js?key=${apiKey}`;
        script.async = true;
        script.defer = true;
        script.onload = initMap;
        script.onerror = () => console.error("Failed to load Google Maps");
        document.head.appendChild(script);
      }
    }
  }, [center, markers, zoom]);

  return <div ref={mapRef} style={{ width: "100%", height }} className="rounded-lg" />;
}

